import React from 'react';
import NumberFormat, { NumberFormatValues } from 'react-number-format'
import Meal from '../meal/meal'
import { config } from '../../dev/config';
import './app.css';



type State = {
  breakfast: number
  lunch: number
  dinner: number
  modifiedFields: string[]
}

export default class App extends React.Component<{}, State> {
  public state: State = {
    breakfast: config.defaultValues.breakfast,
    lunch: config.defaultValues.lunch,
    dinner: config.defaultValues.dinner,
    modifiedFields: [],
  }

  public render(): React.ReactElement {

    return (
      <div className="root">
        <Meal
          changeValue={this.changeValue}
          maxLimit={config.limits.meal}
          suffix={''}
          nutritionValues={this.state.breakfast}
          mealName={'breakfast'}
        />
      </div>
    )
  }

  private changeValue = (value: NumberFormatValues, mealName: string): void => {
    console.log(value, mealName, this.state.modifiedFields)
    this.setState({
      [mealName]: value,
      modifiedFields: mealName,
    } as any)
  }

}
